#!/usr/bin/env python3
"""Republish IMU data with fixed frame_id and optional covariance."""
import rclpy
from rclpy.node import Node
from sensor_msgs.msg import Imu


class ImuRepublisher(Node):
    """Ensure IMU messages have consistent frame_id and covariance."""

    def __init__(self):
        super().__init__("shobot_imu_republisher")
        self.declare_parameter("input_topic", "/imu/raw")
        self.declare_parameter("output_topic", "/imu/data")
        self.declare_parameter("frame_id", "imu_link")
        self.declare_parameter("orientation_covariance", [])
        self.declare_parameter("angular_velocity_covariance", [])
        self.declare_parameter("linear_acceleration_covariance", [])

        input_topic = self.get_parameter("input_topic").value
        output_topic = self.get_parameter("output_topic").value
        self.frame_id = self.get_parameter("frame_id").value

        self.orientation_cov = self._cov_param("orientation_covariance")
        self.angular_vel_cov = self._cov_param("angular_velocity_covariance")
        self.linear_acc_cov = self._cov_param("linear_acceleration_covariance")

        self.publisher = self.create_publisher(Imu, output_topic, 10)
        self.create_subscription(Imu, input_topic, self.imu_cb, 50)

        self.get_logger().info(
            f"Republishing IMU from {input_topic} to {output_topic} with frame_id '{self.frame_id}'"
        )

    def _cov_param(self, name):
        cov = self.get_parameter(name).value
        if cov and len(cov) != 9:
            self.get_logger().warn(f"{name} must have 9 values; ignoring provided value.")
            return []
        return cov

    def imu_cb(self, msg: Imu):
        msg.header.frame_id = self.frame_id

        if self.orientation_cov:
            msg.orientation_covariance = list(self.orientation_cov)
        if self.angular_vel_cov:
            msg.angular_velocity_covariance = list(self.angular_vel_cov)
        if self.linear_acc_cov:
            msg.linear_acceleration_covariance = list(self.linear_acc_cov)

        self.publisher.publish(msg)


def main(args=None):
    rclpy.init(args=args)
    node = ImuRepublisher()
    try:
        rclpy.spin(node)
    except KeyboardInterrupt:
        pass
    finally:
        node.destroy_node()
        rclpy.shutdown()


if __name__ == "__main__":
    main()
